package kata.base

import kata.base.Category.*

class Yahtzee {
    companion object {
        fun score(
            category: Category,
            dice1: Dice,
            dice2: Dice,
            dice3: Dice,
            dice4: Dice,
            dice5: Dice
        ): Int {
            val dices = listOf(
                dice1,
                dice2,
                dice3,
                dice4,
                dice5
            )

            val scoringFn = when (category) {
                CHANCE -> ::chance
                YAHTZEE -> ::yahtzee
                ACES -> ::aces
                TWOS -> ::twos
                THREES -> ::threes
                FOURS -> ::fours
                FIVES -> ::fives
                SIXES -> ::sixes
                THREE_OF_A_KIND -> ::threeOfAKind
                FOUR_OF_A_KIND -> ::fourOfAKind
                FULL_HOUSE -> ::fullHouse
                SMALL_STRAIGHT -> ::smallStraight
                LARGE_STRAIGHT -> ::largeStraight
            }

            return scoringFn(dices)
        }
    }
}


fun main() = print("Hello, Yahtzee!")
