package kata.base

class Yahtzee {
    companion object {
        fun score(score: Score, dice1: Int, dice2: Int, dice3: Int, dice4: Int, dice5: Int) =
            if (score == Score.CHANCE)
                dice1 + dice2 + dice3 + dice4 + dice5
            else 0
    }
}


fun main() = print("Hello, Yahtzee!")
