package kata.base

class Yahtzee {
    companion object {
        fun score(
            category: Category,
            dice1: Dice,
            dice2: Dice,
            dice3: Dice,
            dice4: Dice,
            dice5: Dice
        ): Int {
            val dices = listOf(
                dice1.points,
                dice2.points,
                dice3.points,
                dice4.points,
                dice5.points
            )
            return category.calculateScore(dices)
        }
    }
}


fun main() = print("Hello, Yahtzee!")
